import {CBadge, CButton, CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle} from "@coreui/react";
import React, {useState} from "react";
import DeleteModal from "./DeleteModal";
import DetailModal from "./DetailModal";

const getBadge = status => {
  switch (status) {
    case 'Y':
      return 'success'
    case 'N':
      return 'secondary'
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'productQuantity',
  'costPrice',
  'sellingPrice',
  'status',
  'actions']

const ProductTable = (props) => {

  let productList = props.productList
  const [modal, setModal] = useState(false);
  const [detailModal, setDetailModal] = useState(false);

  const detailModalToggle = () => {
    setDetailModal(!detailModal);

  }

  const toggle = () => {
    setModal(!modal);

  }

  return (
    <React.Fragment>
      <DetailModal
        modal={detailModal}
        toggle={detailModalToggle}/>

      <DeleteModal
        modal={modal}
        toggle={toggle}/>

      <CDataTable
        items={productList}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'status':
            (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>
                  {item.status==='Y'?'Active':'Inactive'}
                </CBadge>
              </td>
            ),
          'actions':
            (item) => (
              <td>

                <CDropdown className="m-1 btn-group">
                  <CDropdownToggle color="primary">
                    Actions
                  </CDropdownToggle>
                  <CDropdownMenu>

                    <CDropdownItem
                      key={item.id}
                      onClick={detailModalToggle}>View</CDropdownItem>

                    <CDropdownItem
                      key={item.id}>Edit</CDropdownItem>

                    <CDropdownItem
                      key={item.id}
                      onClick={toggle(item.id)}>Delete</CDropdownItem>
                  </CDropdownMenu>
                </CDropdown>


              </td>
            )

        }}
      />
    </React.Fragment>
  )
}

export default ProductTable
