import React from 'react'
import {CCard, CCardBody, CCol, CNav, CNavItem, CNavLink, CRow, CTabContent, CTabPane, CTabs,} from '@coreui/react'
import SupplierForm from "./SupplierForm";
import SupplierTable from "./SupplierTable";
import * as axios from "axios";
import DeleteModal from "./DeleteModal";
import ProductTable from "../product/ProductTable";
import SuccessAlert from "../../ui/alerts/SuccessAlert";
import DeleteAlert from "../../ui/alerts/DeleteAlert";

class Supplier extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      supplierList: [],
      deleteModal: false,
      showSuccessAlert: false,
      showDeleteAlert: false,
      addTab:false,
      manageTab:true,

    }


  }

  componentWillMount() {

    let
      supplierSearch = {
        "supplierId": null,
        "supplierName": "",
      }

    let page = 1
    let size = 10

    axios.put('http://localhost:8080/api/v1/supplier/search?page=' + page + "&size=" + size, supplierSearch)
      .then(response => {

        console.log(response.data.responseDTOList)
        const data = response.data.responseDTOList
        this.setState({
          supplierList: data
        })

        console.log("o/p" + this.state.supplierList)
      })
  }

  updateSupplier = (supplierList) => {

    this.setState({
      supplierList: supplierList
    })


  }

  editProduct = () => {
  }

  deleteProduct = (id) => {

    this.setState({
        deleteModal: true
      }
    )


    return (

      <DeleteModal deleteModal={this.props.deleteModal}/>
    )

    console.log("deleteSupplier" + id)
  }

  showAlert = () => {
    this.setState({
      showSuccessAlert: true
    })

  }

  showDeleteAlert = () => {
    this.setState({
      showSuccessAlert: false,
      showDeleteAlert: true
    })

  }

  modalController=()=>{
    alert("modal controller------")
    this.state.setState({
      addTab:false,
      manageTab:true,
    })
  }

  render() {
    return (

      <React.Fragment>
        {this.state.showSuccessAlert === true ?
          <SuccessAlert
            message={"Supplier is added Successfully..."}/>
          : null}

        {this.state.showDeleteAlert === true ?
          <DeleteAlert
            message={"Supplier is deleted..."}/>
          : null}

      <CRow>
        <CCol xs="12" md="12" className="mb-4">
          <CCard>
            <CCardBody>
              <CTabs>
                <CNav variant="tabs">
                  <CNavItem>
                    <CNavLink onClick={this.modalController}>
                      Add Product
                    </CNavLink>
                  </CNavItem>
                  <CNavItem onClick={this.modalController}>
                    <CNavLink>
                      Manage
                    </CNavLink>
                  </CNavItem>
                </CNav>

                <CTabContent>

                  <CTabPane active={this.state.addTab}>
                    <SupplierForm updateSupplier={this.updateSupplier}/>
                  </CTabPane>

                  <CTabPane active={this.state.manageTab}>
                    <SupplierTable
                      showDeleteAlert={this.showDeleteAlert}
                      updateSupplier={this.updateSupplier}
                      deleteSupplier={this.deleteSupplier}
                      editProduct={this.editProduct}
                      supplierList={this.state.supplierList}/>
                  </CTabPane>

                </CTabContent>
              </CTabs>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>

      </React.Fragment>
    )
  }


}

export default Supplier
