import {CBadge, CButton, CCol, CDataTable} from "@coreui/react";
import usersData from "../users/UsersData";
import React from "react";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'status',
  'buttons']


const ProductTable = () => {

  return (
    <CDataTable
      items={usersData}
      fields={fields}
      bordered
      itemsPerPage={5}
      pagination
      scopedSlots={{
        'status':
          (item) => (
            <td>
              <CBadge color={getBadge(item.status)}>
                {item.status}
              </CBadge>
            </td>
          ),
        'buttons':
          () => (
          <td>
            <CCol col="2" sm="2" md="2" xl className="mb-3 mb-xl-0">
              <CButton block color="primary">Primary</CButton>
            </CCol>
            <CCol col="2" sm="2" md="2" xl className="mb-3 mb-xl-0">
              <CButton block color="secondary">Secondary</CButton>
            </CCol>
          </td>
          )

      }}
    />
  )
}

export default ProductTable
