import {CBadge, CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle} from "@coreui/react";
import React from "react";
import * as axios from "core-js";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'productQuantity',
  "costPrice",
  "sellingPrice",
  'actions']

class ProductTable extends React.Component {

  state = {
    productList: []
  }

  componentWillMount() {
    this.fetchAllNews()
  }

  fetchAllNews() {

    let productSearch={
      "productCategory": "",
      "productCode": "",
      "productName": "",
      "productType": ""
    }

    axios.put('http://localhost:8080/api/v1/product/search/',{productSearch})
      // .then(response => response.json())
      .then(response => {
        console.log("productList=" + response.data)

        this.setState({
          productList: response.data
        })
        console.log(this.state.productList)
      })
  }


  render() {
    return (
      <CDataTable
        items={this.state.productList}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'status':
            (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>
                  {item.status}
                </CBadge>
              </td>
            ),
          'actions':
            () => (
              <td>
                <CDropdown className="m-1 btn-group">
                  <CDropdownToggle color="secondary">
                    Actions
                  </CDropdownToggle>
                  <CDropdownMenu>
                    <CDropdownItem>View</CDropdownItem>
                    <CDropdownItem>Edit</CDropdownItem>
                    <CDropdownItem>Delete</CDropdownItem>
                  </CDropdownMenu>
                </CDropdown>
              </td>
            )

        }}
      />
    )
  }
}

export default ProductTable
