import {CBadge, CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle} from "@coreui/react";
import usersData from "../users/UsersData";
import React from "react";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'status',
  'buttons']


const ProductTable = () => {

  return (
    <CDataTable
      items={usersData}
      fields={fields}
      bordered
      itemsPerPage={10}
      pagination
      scopedSlots={{
        'status':
          (item) => (
            <td>
              <CBadge color={getBadge(item.status)}>
                {item.status}
              </CBadge>
            </td>
          ),
        'buttons':
          () => (
            <td>
              <CDropdown className="m-1 btn-group">
                <CDropdownToggle color="secondary">
                  Secondary
                </CDropdownToggle>
                <CDropdownMenu>
                  {/*<CDropdownItem header>Header</CDropdownItem>*/}
                  <CDropdownItem>View</CDropdownItem>
                  <CDropdownItem>Edit</CDropdownItem>
                  <CDropdownItem>Delete</CDropdownItem>
                  {/*<CDropdownDivider />*/}
                  {/*<CDropdownItem>Another Action</CDropdownItem>*/}
                </CDropdownMenu>
              </CDropdown>
            </td>
          )

      }}
    />
  )
}

export default ProductTable
