import React from 'react'
import {
  CAlert,
  CCard,
  CCardBody,
  CCol,
  CNav,
  CNavItem,
  CNavLink,
  CRow,
  CTabContent,
  CTabPane,
  CTabs,
} from '@coreui/react'
import ProductForm from "./ProductForm";
import ProductTable from "./ProductTable";
import * as axios from "axios";
import DeleteModal from "./DeleteModal";

class Product extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      productList: [],
      deleteModal: false,
      showSuccessAlert: false,
      showDeleteAlert: false,
    }
  }



  componentWillMount() {

    let
      productSearch = {
        "productCategoryId": null,
        "productCode": "",
        "productName": "",
        "productType": ""
      }

    let page = 1
    let size = 10

    axios.put('http://localhost:8080/api/v1/product/search?page=' + page + "&size=" + size, productSearch)
      // .then(response => response.json())
      .then(response => {

        console.log(response.data.responseDTOList)
        const data = response.data.responseDTOList
        this.setState({
          productList: data
        })

        console.log("o/p" + this.state.productList)
      })
  }

  updateProduct = (productList) => {

    this.setState({
      productList: productList
    })


  }

  showAlert = () => {
    this.setState({
      showSuccessAlert: true
    })

  }

  showDeleteAlert = () => {
    this.setState({
      showDeleteAlert: true
    })

  }

  editProduct = () => {
  }

  deleteProduct = (id) => {

    this.setState({
        deleteModal: true
      }
    )


    return (

      <DeleteModal deleteModal={this.props.deleteModal}/>
    )

    console.log("deleteProduct" + id)
  }

  render() {
    return (

      <React.Fragment>
        {this.state.showSuccessAlert === true ?
          <CAlert color="success"
                  show="5"
                  closeButton>
            Product added Succesfully...
          </CAlert> : null}

        <CAlert color="danger">
          Product is deleted...
        </CAlert>

        <CRow>
          <CCol xs="12" md="12" className="mb-4">
            <CCard>
              <CCardBody>
                <CTabs>
                  <CNav variant="tabs">
                    <CNavItem>
                      <CNavLink>
                        Add Product
                      </CNavLink>
                    </CNavItem>
                    <CNavItem>
                      <CNavLink>
                        Manage
                      </CNavLink>
                    </CNavItem>
                  </CNav>

                  <CTabContent>

                    <CTabPane>
                      <ProductForm
                        showAlert={this.showAlert}
                        updateProduct={this.updateProduct}/>
                    </CTabPane>

                    <CTabPane>
                      <ProductTable
                        showDeleteAlert={this.showDeleteAlert}
                        updateProduct={this.updateProduct}
                        deleteProduct={this.deleteProduct}
                        editProduct={this.editProduct}
                        productList={this.state.productList}/>
                    </CTabPane>

                  </CTabContent>
                </CTabs>
              </CCardBody>
            </CCard>
          </CCol>
        </CRow>

      </React.Fragment>
    )
  }


}

export default Product
