import React from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CFormGroup,
  CInput,
  CLabel,
  CRow,
  CSelect,
  CTextarea,
} from "@coreui/react";
import CIcon from "@coreui/icons-react";
import * as axios from "axios";

class ProductForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      supplierId: null,
      productCategoryId: null,
      productType: "",
      productName: "",
      productDescription: "",
      productQuantity: "",
      productCostPrice: "",
      productSellingPrice: "",
      productDiscountAmount: "",
      productDiscountPercentage: "",
      supplierList: [],
      productCategoryList: []
    }
  }

  componentDidMount() {

    axios.get('http://localhost:8080/api/v1/supplier/active/min')
      // .then(response => response.json())
      .then(response => {
        const data = response.data

        this.setState({
          supplierList: data
        })
        console.log(this.state.supplierList)
      })

  }

  handleInputChange = (e) => {

    const {name, value} = e.target
    this.setState({
      [name]: value
    })

    if (e.target.name === 'supplierId' && e.target.value !== '-1') {

      const supplierId = e.target.value

      axios.get(`http://localhost:8080/api/v1/product-category/active/${supplierId}`)
        .then(response => {
          const data = response.data

          this.setState({
            productCategoryList: data
          })
          console.log(this.state.productCategoryList)
        })


    }

  }

  handleFormSubmit = (e) => {
    e.preventDefault()

    let product = {
      supplierId: this.state.supplierId,
      productCategoryId: this.state.productCategoryId,
      productType: this.state.productType,
      productName: this.state.productName,
      productDescription: this.state.productDescription,
      productQuantity: this.state.productQuantity,
      productCostPrice: this.state.productCostPrice,
      productSellingPrice: this.state.productSellingPrice,
      productDiscountAmount: this.state.productDiscountAmount,
      productDiscountPercentage: this.state.productDiscountPercentage,
    }

    axios.post('http://localhost:8080/api/v1/product', product)
      // .then(response => response.json())
      .then(response => {

        let
          productSearch = {
            "productCategory": "",
            "productCode": "",
            "productName": "",
            "productType": ""
          }

        let page = 1
        let size = 10

        axios.put('http://localhost:8080/api/v1/product/search?page=' + page + "&size=" + size, productSearch)
          // .then(response => response.json())
          .then(response => {

            console.log(response.data.responseDTOList)
            const data = response.data.responseDTOList

            this.props.updateProduct(data)
          })

      })

    props.showAlert()


  }


  render() {
    return (

      <CRow>

        <CCol xs="12" sm="6">
          <CCard>
            <CCardBody>

              <CFormGroup>
                <CLabel htmlFor="supplierId">Supplier</CLabel>
                <CSelect custom id="supplierId" name="supplierId"
                         value={this.state.supplierId}
                         onChange={this.handleInputChange}
                         placeholder="Enter your company name"
                         required="required">

                  <option value="-1">Please Select Supplier</option>
                  {this.state.supplierList?.map(supplier => {
                    return (
                      <option value={supplier.value}>{supplier.label}</option>
                    )
                  })}

                </CSelect>

              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="productCategory">Product Category</CLabel>
                <CSelect custom id="productCategoryId" name="productCategoryId"
                         value={this.state.productCategoryId}
                         onChange={this.handleInputChange}
                         required="required">

                  {this.state.supplierId === null ?
                    <option value="-1">Please Select Supplier first</option> :
                    <option value="-1">Please Select Product Category</option>}
                  {this.state.productCategoryList.map(productCategory => {
                    return (
                      <option value={productCategory.value}>{productCategory.label}</option>
                    )
                  })}

                </CSelect>

              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="productCategory">Product Type</CLabel>
                <CInput name="productType" id="productType"
                        value={this.state.productType}
                        onChange={this.handleInputChange}
                        placeholder="Enter Product Type"
                        required="required"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="productName">Product Name</CLabel>
                <CInput name="productName" id="productName"
                        value={this.state.productName}
                        onChange={this.handleInputChange}
                        placeholder="Enter Product Name"
                        required="required"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="quantity">Quantity</CLabel>
                <CInput id="quantity" name="productQuantity"
                        value={this.state.productQuantity}
                        onChange={this.handleInputChange}
                        placeholder="Quantity"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="productDescription">Product Description</CLabel>
                <CTextarea name="productDescription" id="productDescription"
                           value={this.state.productDescription}
                           onChange={this.handleInputChange}
                           placeholder="Enter Product Description"
                           required="required"/>
              </CFormGroup>


            </CCardBody>

          </CCard>
        </CCol>

        <CCol xs="12" sm="6">
          <CCard>
            <CCardBody>
              <CFormGroup>
                <CLabel htmlFor="costPrice">Cost Price(Rs.)</CLabel>
                <CInput id="costPrice" name="productCostPrice"
                        value={this.state.productCostPrice}
                        onChange={this.handleInputChange}
                        placeholder="Enter your Cost Price"
                        required="required"/>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="sellingPrice">Selling Price(Rs.)</CLabel>
                <CInput id="sellingPrice" name="productSellingPrice"
                        value={this.state.productSellingPrice}
                        onChange={this.handleInputChange}
                        placeholder="Enter Selling Price"
                        required="required"/>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="productDiscountAmount">Discount Amount</CLabel>
                <CInput id="productDiscountAmount" name="productDiscountAmount"
                        value={this.state.productDiscountAmount}
                        onChange={this.handleInputChange}
                        placeholder="Enter Discount Amount"
                        required="required"/>
              </CFormGroup>
              <CFormGroup row className="my-0">
                <CCol xs="8">
                  <CFormGroup>
                    <CLabel htmlFor="productDiscountPercentage">Discount Percentage(%)</CLabel>
                    <CInput id="productDiscountPercentage" name="productDiscountPercentage"
                            value={this.state.productDiscountPercentage}
                            onChange={this.handleInputChange}
                            placeholder="Enter your city"
                            required="required"/>
                  </CFormGroup>
                </CCol>
              </CFormGroup>

            </CCardBody>
            <CCardFooter>
              <CButton type="submit" onClick={this.handleFormSubmit} size="sm" color="primary"><CIcon
                name="cil-scrubber"/> Submit</CButton>
              <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban"/> Reset</CButton>
            </CCardFooter>

          </CCard>
        </CCol>

      </CRow>


    )
  }
}

export default ProductForm
