import React from 'react'
import {
    CButton,
    CCard,
    CCardBody,
    CCol,
    CForm,
    CFormGroup,
    CInput,
    CInputRadio,
    CLabel,
    CTextarea,
} from "@coreui/react";
import * as axios from "axios";
import CIcon from "@coreui/icons-react";
import Cookies from "universal-cookie";

class PollForm extends React.Component {
    constructor(props) {
        super(props);

        console.log(props)

        this.state = {
            polltitle: "",
            yes: "",
            no: "",
            notificationdate: "",
        }
    }

    handleInputChange = (e) => {

        const {name, value} = e.target
        this.setState({
            [name]: value
        })

    }

    handleFormSubmit = (e) => {
        e.preventDefault()

        const cookies = new Cookies();

        let polls = {
            polltitle: this.state.polltitle,
            yes: this.state.yes,
            no: this.state.no,
            notificationdate: this.state.notificationdate,
        }


        axios.post('http://localhost:3000/polls/', polls,
            {
                headers: {
                    Authorization: cookies.get('token'),
                },
            })
            .then(response => {

                axios.get('http://localhost:3000/polls/')
                    .then(response => {

                        console.log(response.data)
                        const data = response.data

                        this.props.updatePoll(data)

                    })

                this.props.manageTabController()
                this.props.showAlert()

            })
            .catch(function (error) {
                if (error.response) {
                    // Request made and server responded
                    console.log(error.response);
                }

                // throw new Error('Sorry Internal Server Error')
                this.showError(error.response)
            })

    }


    render() {
        return (


            <CCard>
                <CCardBody>

                    <CForm onSubmit={this.handleFormSubmit}>
                        <CFormGroup>
                            <CLabel htmlFor="polltitle">Post tittle</CLabel>
                            <CInput name="polltitle" id="polltitle"
                                    value={this.state.polltitle}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Post Tittle"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup row>
                            <CCol md="3">
                                <CLabel>Radios</CLabel>
                            </CCol>
                            <CCol md="9">
                                <CFormGroup variant="checkbox">
                                    <CInputRadio className="form-check-input" id="radio1" name="radios" value="option1" />
                                    <CLabel variant="checkbox" htmlFor="radio1">Option 1</CLabel>
                                </CFormGroup>
                                <CFormGroup variant="checkbox">
                                    <CInputRadio className="form-check-input" id="radio2" name="radios" value="option2" />
                                    <CLabel variant="checkbox" htmlFor="radio2">Option 2</CLabel>
                                </CFormGroup>
                                <CFormGroup variant="checkbox">
                                    <CInputRadio className="form-check-input" id="radio3" name="radios" value="option3" />
                                    <CLabel variant="checkbox" htmlFor="radio3">Option 3</CLabel>
                                </CFormGroup>
                            </CCol>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="postcategory">Post Category</CLabel>
                            <CInput name="postcategory" id="postcategory"
                                    value={this.state.postcategory}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Post Category"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="postdetails">Post Details</CLabel>
                            <CInput id="postdetails" name="postdetails"
                                    value={this.state.postdetails}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Post Details"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="postconclusion">Post Conclusion</CLabel>
                            <CTextarea name="postconclusion" id="postconclusion"
                                       value={this.state.postconclusion}
                                       onChange={this.handleInputChange}
                                       placeholder="Enter Post Conclusion"
                                       required="required"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="images">Images</CLabel>
                            <CInput type="file" name="files" onChange={this.imageUploader}
                                    placeholder="Enter Post Details"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CButton type="submit" size="sm" color="primary"><CIcon
                                name="cil-scrubber"/> Submit</CButton>
                            <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban"/> Reset</CButton>
                        </CFormGroup>

                    </CForm>
                </CCardBody>

            </CCard>


        )
    }
}

export default PollForm
