import React from 'react'
import {CButton, CCard, CCardBody, CCol, CForm, CFormGroup, CInput, CInputRadio, CLabel,} from "@coreui/react";
import * as axios from "axios";
import CIcon from "@coreui/icons-react";
import Cookies from "universal-cookie";

class PollForm extends React.Component {
    constructor(props) {
        super(props);

        console.log(props)

        this.state = {
            polltitle: "",
            yes: "",
            no: "",
            notificationdate: "",
        }
    }

    handleInputChange = (e) => {

        const {name, value} = e.target
        this.setState({
            [name]: value
        })

    }

    handleFormSubmit = (e) => {
        e.preventDefault()

        const cookies = new Cookies();

        let polls = {
            polltitle: this.state.polltitle,
            yes: this.state.yes,
            no: this.state.no,
            notificationdate: this.state.notificationdate,
        }


        axios.post('http://localhost:3000/polls/', polls,
            {
                headers: {
                    Authorization: cookies.get('token'),
                },
            })
            .then(response => {

                axios.get('http://localhost:3000/polls/')
                    .then(response => {

                        console.log(response.data)
                        const data = response.data

                        this.props.updatePoll(data)

                    })

                this.props.manageTabController()
                this.props.showAlert()

            })
            .catch(function (error) {
                if (error.response) {
                    // Request made and server responded
                    console.log(error.response);
                }

                // throw new Error('Sorry Internal Server Error')
                this.showError(error.response)
            })

    }


    render() {
        return (


            <CCard>
                <CCardBody>

                    <CForm onSubmit={this.handleFormSubmit}>
                        <CFormGroup>
                            <CLabel htmlFor="polltitle">Post tittle</CLabel>
                            <CInput name="polltitle" id="polltitle"
                                    value={this.state.polltitle}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Post Tittle"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup row>
                            <CCol md="3">
                                <CLabel>Answer(Yes/No)</CLabel>

                                <CFormGroup variant="custom-radio" inline>
                                    <CInputRadio custom id="inline-radio1"
                                                 name="inline-radios"
                                                 value="1"
                                                 onChange={this.handleInputChange}
                                                 value={this.state.yes}/>
                                    <CLabel variant="custom-checkbox" htmlFor="inline-radio1">Yes</CLabel>
                                </CFormGroup>
                                <CFormGroup variant="custom-radio" inline>
                                    <CInputRadio custom id="inline-radio2"
                                                 name="inline-radios"
                                                 value="1"
                                                 onChange={this.handleInputChange}
                                                 value={this.state.no}/>
                                    <CLabel variant="custom-checkbox" htmlFor="inline-radio2">No</CLabel>
                                </CFormGroup>
                            </CCol>

                        </CFormGroup>

                        <CFormGroup>
                            <CButton type="submit" size="sm" color="primary"><CIcon
                                name="cil-scrubber"/> Submit</CButton>
                            <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban"/> Reset</CButton>
                        </CFormGroup>

                    </CForm>
                </CCardBody>

            </CCard>


        )
    }
}

export default PollForm
