import React from 'react'
import {CButton, CModal, CModalBody, CModalFooter, CModalHeader} from "@coreui/react";
import * as axios from "axios";

class DeleteModal extends React.Component{

  constructor(props) {
    super(props);

  }

  handleDeleteBtn = (id) => {

    const
      deleteRequest= {
        "id": id,
        "remarks": "Supplier is deleted...",
        "status": 'D'
      }

    let page = 1
    let size = 10

    axios.delete(`http://localhost:3000/posts/${this.props.selectedItem}`)
      .then(response => {

        let
          supplierSearch = {
            "supplierId": null,
            "supplierName": "",
          }

        let page = 1
        let size = 10

        axios.put('http://localhost:8080/api/v1/supplier/search?page=' + page + "&size=" + size, supplierSearch)
          .then(response => {


            console.log(response.data.responseDTOList)
            const data = response.data.responseDTOList

            this.props.updateSupplier(data)
          })


        this.props.toggle()

        this.props.showDeleteAlert()

      })


  }

  render() {

    return (
      <>
        <CModal
          show={this.props.modal}
          onClose={this.props.toggle}
        >
          <CModalHeader closeButton>Delete Product</CModalHeader>
          <CModalBody>
            Are you sure want to delete?
          </CModalBody>
          <CModalFooter>

            <CButton
              color="secondary"
              onClick={this.props.toggle}
            >Cancel</CButton>{' '}
            <CButton onClick={()=>this.handleDeleteBtn(this.props.selectedItem)} color="danger">Delete</CButton>
          </CModalFooter>
        </CModal>
      </>
    )
  }
}

export default DeleteModal
