import React from 'react'
import {CButton, CCard, CCardBody, CForm, CFormGroup, CInput, CLabel, CTextarea,} from "@coreui/react";
import * as axios from "axios";
import CIcon from "@coreui/icons-react";

class NewsForm extends React.Component {
  constructor(props) {
    super(props);

    console.log(props)

    this.state = {
      posttitle: "",
      postcategory: "",
      postdetails: "",
      postconclusion: ""
    }
  }

  handleInputChange = (e) => {

    const {name, value} = e.target
    this.setState({
      [name]: value
    })

  }

   showError(message) {
    this.props.showErrorAlert(message)

  }

  handleFormSubmit = (e) => {
    e.preventDefault()

    let news = {
      posttitle:  this.state.posttitle,
      postcategory:  this.state.postcategory,
      postdetails:  this.state.postdetails,
      postconclusion:  this.state.postconclusion,
    }

    axios.post('http://localhost:3000/posts/', news)
      .then(response => {

        axios.get('http://localhost:3000/usersnews/')
            .then(response => {

              console.log(response.data)
              const data = response.data

              this.props.updateNews(data)

              console.log("o/p" + this.state.newsList)
            })

        this.props.manageTabController()

        this.props.showAlert()

      })
      .catch(function (error) {
        if (error.response) {
          // Request made and server responded
          console.log(error.response.data.errorMessage);
        }

        // throw new Error('Sorry Internal Server Error')
       this.showError(error.response.data.errorMessage)
      })

  }


  render() {
    return (


      <CCard>
        <CCardBody>

          <CForm onSubmit={this.handleFormSubmit}>
            <CFormGroup>
              <CLabel htmlFor="posttitle">News Name</CLabel>
              <CInput name="posttitle" id="posttitle"
                      value={this.state.posttitle}
                      onChange={this.handleInputChange}
                      placeholder="Enter Post Tittle"
                      required="required"/>
            </CFormGroup>

            <CFormGroup>
              <CLabel htmlFor="postcategory">News Address</CLabel>
              <CInput name="postcategory" id="postcategory"
                      value={this.state.postcategory}
                      onChange={this.handleInputChange}
                      placeholder="Enter Post Category"
                      required="required"/>
            </CFormGroup>

            <CFormGroup>
              <CLabel htmlFor="postdetails">Post Details</CLabel>
              <CInput id="postdetails" name="postdetails"
                      value={this.state.supplierPanNumber}
                      onChange={this.handleInputChange}
                      placeholder="Enter News Pan Number"/>
            </CFormGroup>

            <CFormGroup>
              <CLabel htmlFor="supplierPhoneNumber">News Phone Number</CLabel>
              <CTextarea name="supplierPhoneNumber" id="supplierPhoneNumber"
                         value={this.state.supplierPhoneNumber}
                         onChange={this.handleInputChange}
                         placeholder="Enter News Phone Number"
                         required="required"/>
            </CFormGroup>

            <CFormGroup>
              <CLabel htmlFor="supplierMobileNumber">News Mobile Number</CLabel>
              <CTextarea name="supplierMobileNumber" id="supplierMobileNumber"
                         value={this.state.supplierMobileNumber}
                         onChange={this.handleInputChange}
                         placeholder="Enter News Mobile Number"
                         required="required"/>
            </CFormGroup>

            <CFormGroup>
              <CLabel htmlFor="supplierEmail">News Email</CLabel>
              <CTextarea name="supplierEmail" id="supplierEmail"
                         value={this.state.supplierEmail}
                         onChange={this.handleInputChange}
                         placeholder="Enter News Email"
                         required="required"/>
            </CFormGroup>

            <CFormGroup>
              <CLabel htmlFor="supplierWebsiteUrl">News Website</CLabel>
              <CTextarea name="supplierWebsiteUrl" id="supplierWebsiteUrl"
                         value={this.state.supplierWebsiteUrl}
                         onChange={this.handleInputChange}
                         placeholder="Enter News Website"
                         required="required"/>
            </CFormGroup>

            <CFormGroup>
              <CButton type="submit" size="sm" color="primary"><CIcon
                name="cil-scrubber"/> Submit</CButton>
              <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban"/> Reset</CButton>
            </CFormGroup>

          </CForm>
        </CCardBody>

      </CCard>


    )
  }
}

export default NewsForm
