import React from 'react'
import {
    CButton,
    CCard,
    CCardBody,
    CCol,
    CFormGroup,
    CInput,
    CLabel,
    CModal,
    CModalBody,
    CModalFooter,
    CModalHeader,
    CRow,
    CTextarea
} from "@coreui/react";
import * as axios from "axios";
import Cookies from "universal-cookie";

class DetailModal extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            news: {}
        }
    }

    componentWillMount() {
        const cookies = new Cookies();

        axios.get(`http://localhost:3000/posts/${this.props.selectedItem}`,
            {
                headers: {

                    Authorization: cookies.get('token')
                }
            }
        )
            .then(response => {

                this.setState({
                    news: response.data
                })

            })
    }

    render() {

        return (
            <>
                <CModal size={'xl'}
                        show={this.props.detailModal}
                        onClose={this.props.detailModalToggle}
                >
                    <CModalHeader closeButton>News Detail</CModalHeader>
                    <CModalBody>

                        <CRow>

                            <CCol xs="12" sm="6">
                                <CCard>
                                    <CCardBody>

                                        <CFormGroup>
                                            <CLabel htmlFor="posttitle">Post Tittle</CLabel>
                                            <CInput name="posttitle"
                                                    value={this.state.news.posttitle} disabled="disabled"/>
                                        </CFormGroup>
                                        <CFormGroup>
                                            <CLabel htmlFor="productCategory">Post Category</CLabel>
                                            <CInput name="postcategory"
                                                    value={this.state.news.postcategory} disabled="disabled"/>
                                        </CFormGroup>

                                        <CFormGroup>
                                            <CLabel htmlFor="postdetails">Post Details</CLabel>
                                            <CInput name="postdetails" value={this.state.news.postdetails} disabled="disabled"/>
                                        </CFormGroup>

                                        <CFormGroup>
                                            <CLabel htmlFor="postconclusion">Post Conclusion</CLabel>
                                            <CInput name="postconclusion"
                                                    value={this.state.news.postconclusion} disabled="disabled"/>
                                        </CFormGroup>


                                    </CCardBody>

                                </CCard>
                            </CCol>

                        </CRow>

                    </CModalBody>
                    <CModalFooter>

                        <CButton
                            color="secondary"
                            onClick={this.props.detailModalToggle}
                        >Cancel</CButton>{' '}
                    </CModalFooter>
                </CModal>
            </>
        )
    }

}

export default DetailModal
