import React from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCol, CModal, CModalBody, CModalFooter, CModalHeader,
  CNav,
  CNavItem,
  CNavLink,
  CRow,
  CTabContent,
  CTabPane,
  CTabs,
} from '@coreui/react'
import ProductForm from "./ProductForm";
import ProductTable from "./ProductTable";
import * as axios from "axios";
import DeleteModal from "./DeleteModal";

class Product extends React.Component {

  constructor(props) {
    super(props);

    // const [active, setActive] = useState(1)
    // const lorem = 'Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit.'

    this.state = {
      productList: [],
      deleteModal: false
    }


  }

  componentWillMount() {

    let
      productSearch = {
        "productCategory": "",
        "productCode": "",
        "productName": "",
        "productType": ""
      }

    let page = 1
    let size = 10

    axios.put('http://localhost:8080/api/v1/product/search?page=' + page + "&size=" + size, productSearch)
      // .then(response => response.json())
      .then(response => {

        console.log(response.data.responseDTOList)
        const data = response.data.responseDTOList
        this.setState({
          productList: data
        })

        console.log("o/p" + this.state.productList)
      })
  }

  updateProduct = (productList) => {

    this.setState({
      productList: productList
    })


  }

  editProduct = () => {
  }

  deleteProduct = (id) => {

    this.setState({
        deleteModal: true
      }
    )

    return (
      <>
        <CButton
          onClick={toggle}
          className="mr-1"
        >Launch demo modal</CButton>
        <CModal
          show={modal}
          onClose={toggle}
        >
          <CModalHeader closeButton>Modal title</CModalHeader>
          <CModalBody>
            Lorem ipsum dolor...
          </CModalBody>
          <CModalFooter>
            <CButton color="primary">Do Something</CButton>{' '}
            <CButton
              color="secondary"
              onClick={toggle}
            >Cancel</CButton>
          </CModalFooter>
        </CModal>
      </>
    )

    console.log("deleteProduct" + id)
  }

  render() {
    return (

      <CRow>
        <CCol xs="12" md="12" className="mb-4">
          <CCard>
            {/*<CCardHeader>*/}
            {/*  Index indentifiers*/}
            {/*</CCardHeader>*/}
            <CCardBody>
              <CTabs>
                <CNav variant="tabs">
                  <CNavItem>
                    <CNavLink>
                      Add Product
                    </CNavLink>
                  </CNavItem>
                  <CNavItem>
                    <CNavLink>
                      Manage
                    </CNavLink>
                  </CNavItem>
                </CNav>

                <CTabContent>

                  <CTabPane>
                    <ProductForm updateProduct={this.updateProduct}/>
                  </CTabPane>

                  <CTabPane>
                    <ProductTable
                      deleteProduct={this.deleteProduct}
                      editProduct={this.editProduct}
                      productList={this.state.productList}/>
                  </CTabPane>

                </CTabContent>
              </CTabs>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>
    )
  }


}

export default Product
