import React from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CFormGroup,
  CInput,
  CLabel,
  CRow,
  CSelect,
  CTextarea,
} from "@coreui/react";
import CIcon from "@coreui/icons-react";
import * as axios from "axios";

class ProductForm extends React.Component {
  constructor() {
    super();

    this.state = {
        supplierId: "",
        productCategory: "",
        productType: "",
        productName: "",
        productDescription: "",
        productQuantity: "",
        productCostPrice: "",
        productSellingPrice: "",
        productDiscountAmount: "",
        productDiscountPercentage: "",
      supplierList: []
    }

  }


  componentWillMount() {

    axios.get('http://localhost:8080/api/v1/supplier/active/min?')
      // .then(response => response.json())
      .then(response => {
        const data = response.data

        this.setState({
          supplierId: data
        })
        console.log(this.state.supplierId)
      })

  }

  handleInputChange = (e) => {

    const {name, value} = e.target
    this.setState({
      product: {
        [name]: value
      }
    })

    console.log(this.state.product)


  }

  handleFormSubmit = (e) => {
    e.preventDefault()

    let product = {
      supplierId: this.state.product.supplierId,
      productCategory: this.state.product.productCategory,
      productType: this.state.product.productType,
      productName: this.state.product.productName,
      productDescription: this.state.product.productDescription,
      productQuantity: this.state.product.productQuantity,
      productCostPrice: this.state.product.productCostPrice,
      productSellingPrice: this.state.product.productSellingPrice,
      productDiscountAmount: this.state.product.productDiscountAmount,
      productDiscountPercentage: this.state.product.productDiscountPercentage,
    }

    alert("form submit i called" + product)

    axios.post('http://localhost:8080/api/v1/product', product)
      // .then(response => response.json())
      .then(response => {
        const data = response.data
        console.log(data)
      })

  }


  render() {
    return (

      <CRow>

        <CCol xs="12" sm="6">
          <CCard>
            <CCardBody>

              <CFormGroup>
                <CLabel htmlFor="supplierId">Supplier</CLabel>
                <CSelect custom id="supplierId" name="supplierId"
                         value={this.state.product.supplierId}
                         onChange={this.handleInputChange}
                         placeholder="Enter your company name">

                  <option value="-1">Please Select Supplier</option>
                  {this.state.supplierId.map(supplier => {
                    return (
                      <option value={supplier.key}>{supplier.label}</option>
                    )
                  })}

                </CSelect>

              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="productCategory">Product Category</CLabel>
                <CInput name="productCategory" id="productCategory"
                        value={this.state.product.productCategory}
                        onChange={this.handleInputChange}
                        placeholder="Enter Product Category"/>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="productCategory">Product Type</CLabel>
                <CInput name="productType" id="productType"
                        value={this.state.product.productType}
                        onChange={this.handleInputChange}
                        placeholder="Enter Product Type"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="productName">Product Name</CLabel>
                <CInput name="productName" id="productName"
                        value={this.state.product.productName}
                        onChange={this.handleInputChange}
                        placeholder="Enter Product Name"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="quantity">Quantity</CLabel>
                <CInput id="quantity" name="productQuantity"
                        value={this.state.product.productQuantity}
                        onChange={this.handleInputChange}
                        placeholder="Quantity"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="productDescription">Product Description</CLabel>
                <CTextarea name="productDescription" id="productDescription"
                           value={this.state.product.productDescription}
                           onChange={this.handleInputChange}
                           placeholder="Enter Product Description"/>
              </CFormGroup>


            </CCardBody>

          </CCard>
        </CCol>

        <CCol xs="12" sm="6">
          <CCard>
            <CCardBody>
              <CFormGroup>
                <CLabel htmlFor="costPrice">Cost Price(Rs.)</CLabel>
                <CInput id="costPrice" name="costPrice"
                        value={this.state.product.productCostPrice}
                        onChange={this.handleInputChange}
                        placeholder="Enter your Cost Price"/>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="sellingPrice">Selling Price(Rs.)</CLabel>
                <CInput id="costPrice" name="costPrice"
                        value={this.state.product.productSellingPrice}
                        onChange={this.handleInputChange}
                        placeholder="Enter Selling Price"/>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="productDiscountAmount">Discount Amount</CLabel>
                <CInput id="productDiscountAmount" name="productDiscountAmount"
                        value={this.state.product.productDiscountAmount}
                        onChange={this.handleInputChange}
                        placeholder="Enter Discount Amount"/>
              </CFormGroup>
              <CFormGroup row className="my-0">
                <CCol xs="8">
                  <CFormGroup>
                    <CLabel htmlFor="productDiscountPercentage">Discount Percentage(%)</CLabel>
                    <CInput id="productDiscountPercentage" name="productDiscountPercentage"
                            value={this.state.product.productDiscountPercentage}
                            onChange={this.handleInputChange}
                            placeholder="Enter your city"/>
                  </CFormGroup>
                </CCol>
              </CFormGroup>

            </CCardBody>
            <CCardFooter>
              <CButton type="submit" onClick={this.handleFormSubmit} size="sm" color="primary"><CIcon
                name="cil-scrubber"/> Submit</CButton>
              <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban"/> Reset</CButton>
            </CCardFooter>

          </CCard>
        </CCol>

      </CRow>


    )
  }
}

export default ProductForm
