import {CBadge, CButton, CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle} from "@coreui/react";
import React, {useState} from "react";
import DeleteModal from "./modals/DeleteModal";
import DetailModal from "./modals/DetailModal";
import * as axios from "axios";

const getBadge = status => {
  switch (status) {
    case 'Y':
      return 'success'
    case 'N':
      return 'secondary'
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'productQuantity',
  'costPrice',
  'sellingPrice',
  'status',
  'actions']

const ProductTable = (props) => {

  let productList = props.productList
  const [modal, setModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(false);
  const [detailModal, setDetailModal] = useState(false);

  const detailModalToggle = (id) => {


    axios.get(`http://localhost:8080/api/v1/product/${this.props.selectedItem}`)
      .then(response => {
        const data = response.data

        setSelectedProduct(id)
      })

    setDetailModal(!detailModal);

  }

  const toggle = (id) => {

    setSelectedItem(id)
    setModal(!modal);

  }

  return (
    <React.Fragment>

      <DetailModal
        selectedItem={selectedItem}
        detailModal={detailModal}
        detailModalToggle={detailModalToggle}/>


      <DeleteModal
        showDeleteAlert={props.showDeleteAlert}
        updateProduct={props.updateProduct}
        selectedItem={selectedItem}
        modal={modal}
        toggle={toggle}/>

      <CDataTable
        items={productList}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'status':
            (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>
                  {item.status==='Y'?'Active':'Inactive'}
                </CBadge>
              </td>
            ),
          'actions':
            (item) => (
              <td>

                <CDropdown className="m-1 btn-group">
                  <CDropdownToggle color="primary">
                    Actions
                  </CDropdownToggle>
                  <CDropdownMenu>

                    <CDropdownItem
                      onClick={()=>detailModalToggle(item.id)}>View</CDropdownItem>

                    <CDropdownItem>Edit</CDropdownItem>

                    <CDropdownItem
                      onClick={()=>toggle(item.id)}>Delete</CDropdownItem>
                  </CDropdownMenu>
                </CDropdown>


              </td>
            )

        }}
      />
    </React.Fragment>
  )
}

export default ProductTable
