import {
  CBadge,
  CButton,
  CDataTable,
  CDropdown, CDropdownItem, CDropdownMenu,
  CDropdownToggle,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader
} from "@coreui/react";
import React, {useState} from "react";
import DeleteModal from "./DeleteModal";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'productQuantity',
  "costPrice",
  "sellingPrice",
  'actions']

const ShowDeleteModal = (modal, toggle) => {

  return (

    <CModal
      show={modal}
      onClose={toggle}
    >
      <CModalHeader closeButton>Modal title</CModalHeader>
      <CModalBody>
        Lorem ipsum dolor...
      </CModalBody>
      <CModalFooter>
        <CButton color="primary">Do Something</CButton>{' '}
        <CButton
          color="secondary"
          onClick={toggle}
        >Cancel</CButton>
      </CModalFooter>
    </CModal>

  )
}

const ProductCategoryTable = (props) => {

  let productList = props.productList
  const [modal, setModal] = useState(false);
  const toggle = () => {
    setModal(!modal);

  }


  return (
    <React.Fragment>
      <DeleteModal modal={modal}
                   toggle={toggle}/>

      <CDataTable
        items={productList}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'status':
            (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>
                  {item.status}
                </CBadge>
              </td>
            ),
          'actions':
            (item) => (
              <td>

                <CDropdown className="m-1 btn-group">
                  <CDropdownToggle color="primary">
                    Actions
                  </CDropdownToggle>
                  <CDropdownMenu>

                    <CDropdownItem
                      key={item.id}
                      onClick={detailModalToggle}>View</CDropdownItem>

                    <CDropdownItem
                      key={item.id}>Edit</CDropdownItem>

                    <CDropdownItem
                      key={item.id}
                      onClick={toggle}>Delete</CDropdownItem>
                  </CDropdownMenu>
                </CDropdown>


              </td>
            )

        }}
      />
    </React.Fragment>
  )
}

export default ProductCategoryTable
