import React from 'react'
import {CCard, CCardBody, CCol, CNav, CNavItem, CNavLink, CRow, CTabContent, CTabPane, CTabs,} from '@coreui/react'
import SupplierForm from "./SupplierForm";
import SupplierTable from "./SupplierTable";
import * as axios from "axios";
import DeleteModal from "./DeleteModal";
import SuccessAlert from "../../ui/alerts/SuccessAlert";
import DeleteAlert from "../../ui/alerts/DeleteAlert";

class Supplier extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      supplierList: [],
      deleteModal: false,
      showSuccessAlert: false,
      showDeleteAlert: false,
      errorMessage:{
        showErrorAlert: false,
        message: ''
      },
      modal: {
        addNavLink: true,
        manageNavLink: false,
        addTab: true,
        manageTab: false
      }


    }


  }

  componentWillMount() {

    let
      supplierSearch = {
        "supplierId": null,
        "supplierName": "",
      }

    let page = 1
    let size = 10

    axios.put('http://localhost:8080/api/v1/supplier/search?page=' + page + "&size=" + size, supplierSearch)
      .then(response => {

        console.log(response.data.responseDTOList)
        const data = response.data.responseDTOList
        this.setState({
          supplierList: data
        })

        console.log("o/p" + this.state.supplierList)
      })
  }

  updateSupplier = (supplierList) => {

    this.setState({
      supplierList: supplierList
    })


  }

  editProduct = () => {
  }

  deleteProduct = (id) => {

    this.setState({
        deleteModal: true
      }
    )


    return (

      <DeleteModal deleteModal={this.props.deleteModal}/>
    )

    console.log("deleteSupplier" + id)
  }

  showAlert = () => {
    this.setState({
      showSuccessAlert: true
    })

  }

  showDeleteAlert = () => {
    this.setState({
      showSuccessAlert: false,
      showDeleteAlert: true
    })

  }

  showErrorAlert = (message) => {
    this.setState({
      errorMessage:{
        showErrorAlert: true,
        message: message
      }
    })

  }

  addTabController = () => {
    this.setState({
      modal: {
        addNavLink: true,
        addTab: true,
        manageNavLink: false,
        manageTab: false
      }
    })
  }

  manageTabController = () => {
    this.setState({
      modal: {
        addNavLink: false,
        addTab: false,
        manageNavLink: true,
        manageTab: true
      }
    })
  }


  render() {
    return (

      <React.Fragment>
        {this.state.errorMessage.showErrorAlert === true ?
          <DeleteAlert
            message={this.state.errorMessage.showErrorAlert.errorMessage}/>
          : null}

        {this.state.showSuccessAlert === true ?
          <SuccessAlert
            message={"Supplier is added Successfully..."}/>
          : null}

        {this.state.showDeleteAlert === true ?
          <DeleteAlert
            message={"Supplier is deleted..."}/>
          : null}

        <CRow>
          <CCol xs="12" md="12" className="mb-4">
            <CCard>
              <CCardBody>
                <CTabs>
                  <CNav variant="tabs">
                    <CNavItem onClick={this.addTabController}>
                      <CNavLink active={this.state.modal.addNavLink}>
                        Add Product
                      </CNavLink>
                    </CNavItem>
                    <CNavItem onClick={this.manageTabController}>
                      <CNavLink active={this.state.modal.manageNavLink}>
                        Manage
                      </CNavLink>
                    </CNavItem>
                  </CNav>

                  <CTabContent>

                    <CTabPane active={this.state.modal.addTab}>
                      <SupplierForm
                        showErrorAlert={mesage=>this.showErrorAlert(mesage)}
                        showAlert={this.showAlert}
                        manageTabController={this.manageTabController}
                        updateSupplier={this.updateSupplier}/>
                    </CTabPane>

                    <CTabPane active={this.state.modal.manageTab}>
                      <SupplierTable
                        showDeleteAlert={this.showDeleteAlert}
                        updateSupplier={this.updateSupplier}
                        deleteSupplier={this.deleteSupplier}
                        editProduct={this.editProduct}
                        supplierList={this.state.supplierList}/>
                    </CTabPane>

                  </CTabContent>
                </CTabs>
              </CCardBody>
            </CCard>
          </CCol>
        </CRow>

      </React.Fragment>
    )
  }


}

export default Supplier
