import React from 'react'
import {
    CButton,
    CCard,
    CCardBody,
    CCol, CForm,
    CFormGroup,
    CInput,
    CLabel,
    CModal,
    CModalBody,
    CModalFooter,
    CModalHeader,
    CRow, CTextarea
} from "@coreui/react";
import * as axios from "axios";
import Cookies from "universal-cookie";
import CIcon from "@coreui/icons-react";

class EditModal extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            news: {},
                posttitle: "",
                postcategory: "",
                postdetails: "",
                postconclusion: "",
                uploading: false,
                file: null
        }
    }

    handleInputChange = (e) => {

        const {name, value} = e.target
        this.setState({
            [name]: value
        })

    }

    handleFormSubmit = (e) => {
        e.preventDefault()

        const cookies = new Cookies();

        const formData = new FormData();
        formData.append('images',this.state.file);

        let news = {
            posttitle: this.state.posttitle,
            postcategory: this.state.postcategory,
            postdetails: this.state.postdetails,
            postconclusion: this.state.postconclusion,
            // images: this.state.file
        }


        axios.put('http://localhost:3000/posts/', news,formData,
            {
                headers: {
                    Authorization: cookies.get('token'),
                },
            })
            .then(response => {

                axios.get(`http://localhost:3000/usersnews/${this.props.selectedItem}`)
                    .then(response => {

                        console.log(response.data)
                        const data = response.data

                        this.props.updateNews(data)

                        console.log("o/p" + this.state.newsList)
                    })

                this.props.detailModalToggle()
                this.props.showAlert()

            })
            .catch(function (error) {
                if (error.response) {
                    // Request made and server responded
                    console.log(error.response);
                }

                // throw new Error('Sorry Internal Server Error')
                this.showError(error.response)
            })

    }

    componentWillMount() {
        const cookies = new Cookies();

        axios.get(`http://localhost:3000/posts/${this.props.selectedItem}`,
            {
                headers: {
                    Authorization: cookies.get('token')
                }
            }
        )
            .then(response => {

                this.setState({
                    news: response.data
                })

            })
    }

    modalCancelBtn = () => {

        this.props.selectedItem=null
        this.props.detailModalToggle()
    }

    render() {

        return (
            <>
                <CModal size={'xl'}
                        show={this.props.editToggle}
                        onClose={this.props.editModalToggle}
                >
                    <CModalHeader closeButton>News Detail</CModalHeader>
                    <CModalBody>

                        <CForm onSubmit={this.handleFormSubmit}>
                            <CFormGroup>
                                <CLabel htmlFor="posttitle">Post tittle</CLabel>
                                <CInput name="posttitle" id="posttitle"
                                        value={this.state.news.posttitle}
                                        onChange={this.handleInputChange}
                                        placeholder="Enter Post Tittle"
                                        required="required"/>
                            </CFormGroup>

                            <CFormGroup>
                                <CLabel htmlFor="postcategory">Post Category</CLabel>
                                <CInput name="postcategory" id="postcategory"
                                        value={this.state.news.postcategory}
                                        onChange={this.handleInputChange}
                                        placeholder="Enter Post Category"
                                        required="required"/>
                            </CFormGroup>

                            <CFormGroup>
                                <CLabel htmlFor="postdetails">Post Details</CLabel>
                                <CInput id="postdetails" name="postdetails"
                                        value={this.state.news.postdetails}
                                        onChange={this.handleInputChange}
                                        placeholder="Enter Post Details"/>
                            </CFormGroup>

                            <CFormGroup>
                                <CLabel htmlFor="postconclusion">Post Conclusion</CLabel>
                                <CTextarea name="postconclusion" id="postconclusion"
                                           value={this.state.news.postconclusion}
                                           onChange={this.handleInputChange}
                                           placeholder="Enter Post Conclusion"
                                           required="required"/>
                            </CFormGroup>

                            <CFormGroup>
                                <CLabel htmlFor="images">Images</CLabel>
                                <CInput type="file" name="files" onChange={this.imageUploader}
                                        placeholder="Enter Post Details"/>
                            </CFormGroup>

                            <CFormGroup>
                                <CButton type="submit" size="sm" color="primary"><CIcon
                                    name="cil-scrubber"/> Submit</CButton>
                                <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban"/> Reset</CButton>
                            </CFormGroup>

                        </CForm>

                    </CModalBody>
                </CModal>
            </>
        )
    }

}

export default EditModal
