import {CBadge, CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle} from "@coreui/react";
import React, {useState} from "react";
import DeleteModal from "./modals/DeleteModal";
import DetailModal from "./modals/DetailModal";

const getBadge = status => {
  switch (status) {
    case 'Y':
      return 'success'
    case 'N':
      return 'secondary'
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}

const fields = [

  'posttitle',
  'postcategory',
  'postdetails',
  'postconclusion',
  'images',
  'actions']

const NewsTable = (props) => {

  let newsList = props.newsList
  const [modal, setModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(false);
  const [detailModal, setDetailModal] = useState(false);

  const detailModalToggle = (id) => {
    setSelectedItem(id)
    setDetailModal(!detailModal);

  }

  const toggle = (id) => {

    setSelectedItem(id)
    setModal(!modal);

  }

  return (
    <React.Fragment>
      <DetailModal
        modal={detailModal}
        toggle={detailModalToggle}/>
      <DeleteModal
        showDeleteAlert={props.showDeleteAlert}
        updateNews={props.updateNews}
        selectedItem={selectedItem}
        modal={modal}
        toggle={toggle}/>

      <CDataTable
        items={newsList}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'actions':
            (item) => (
              <td>

                <CDropdown className="m-1 btn-group">
                  <CDropdownToggle color="primary">
                    Actions
                  </CDropdownToggle>
                  <CDropdownMenu>

                    <CDropdownItem
                      onClick={detailModalToggle}>View</CDropdownItem>

                    <CDropdownItem>Edit</CDropdownItem>

                    <CDropdownItem
                      key={item._id}
                      onClick={()=>toggle(item._id)}>Delete</CDropdownItem>
                  </CDropdownMenu>
                </CDropdown>


              </td>
            )

        }}
      />
    </React.Fragment>
  )
}

export default NewsTable
