import {CBadge, CButton, CCol, CDataTable, CRow} from "@coreui/react";
import usersData from "../users/UsersData";
import React from "react";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = ['productName', 'productCategory', 'productType', 'status']


const ProductTable=()=>{

  return(
    <CDataTable
      items={usersData}
      fields={fields}
      bordered
      itemsPerPage={5}
      pagination
      scopedSlots = {{
        'status':
          (item)=>(
            <td>
              <CBadge color={getBadge(item.status)}>
                {item.status}
              </CBadge>
            </td>
          )

      }}
    />
  )
}

export default ProductTable
