import React from 'react'
import {CCard, CCardBody, CCol, CNav, CNavItem, CNavLink, CRow, CTabContent, CTabPane, CTabs,} from '@coreui/react'
import SupplierForm from "./SupplierForm";
import SupplierTable from "./SupplierTable";
import * as axios from "axios";
import DeleteModal from "./DeleteModal";

class Supplier extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      supplierList: [],
      deleteModal: false
    }


  }

  componentWillMount() {

    let
      supplierSearch = {
        "supplierId": null,
        "supplierName": "",
        "productName": "",
        "productType": ""
      }

    let page = 1
    let size = 10

    axios.put('http://localhost:8080/api/v1/supplier/search?page=' + page + "&size=" + size, supplierSearch)
      .then(response => {

        console.log(response.data.responseDTOList)
        const data = response.data.responseDTOList
        this.setState({
          supplierList: data
        })

        console.log("o/p" + this.state.supplierList)
      })
  }

  updateProduct = (supplierList) => {

    this.setState({
      supplierList: supplierList
    })


  }

  editProduct = () => {
  }

  deleteProduct = (id) => {

    this.setState({
        deleteModal: true
      }
    )


    return (

      <DeleteModal deleteModal={this.props.deleteModal}/>
    )

    console.log("deleteSupplier" + id)
  }

  render() {
    return (

      <CRow>
        <CCol xs="12" md="12" className="mb-4">
          <CCard>
            {/*<CCardHeader>*/}
            {/*  Index indentifiers*/}
            {/*</CCardHeader>*/}
            <CCardBody>
              <CTabs>
                <CNav variant="tabs">
                  <CNavItem>
                    <CNavLink>
                      Add Product
                    </CNavLink>
                  </CNavItem>
                  <CNavItem>
                    <CNavLink>
                      Manage
                    </CNavLink>
                  </CNavItem>
                </CNav>

                <CTabContent>

                  <CTabPane>
                    <SupplierForm updateProduct={this.updateProduct}/>
                  </CTabPane>

                  <CTabPane>
                    <SupplierTable
                      deleteProduct={this.deleteProduct}
                      editProduct={this.editProduct}
                      productList={this.state.supplierList}/>
                  </CTabPane>

                </CTabContent>
              </CTabs>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>
    )
  }


}

export default Supplier
