import React from 'react'
import {
    CButton,
    CCard,
    CCardBody,
    CCol, CForm,
    CFormGroup,
    CInput, CInputRadio,
    CLabel,
    CModal,
    CModalBody,
    CModalFooter,
    CModalHeader,
    CRow
} from "@coreui/react";
import * as axios from "axios";
import Cookies from "universal-cookie";

class DetailModal extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            poll: {}
        }
    }

    componentWillMount() {
        const cookies = new Cookies();

        axios.get(`http://localhost:3000/polls/${this.props.selectedItem}`,
            {
                headers: {
                    Authorization: cookies.get('token')
                }
            }
        )
            .then(response => {

                this.setState({
                    poll: response.data
                })

            })
    }

    modalCancelBtn = () => {

        this.props.selectedItem=null
        this.props.detailModalToggle()
    }

    render() {

        return (
            <>
                <CModal size={'xl'}
                        show={this.props.detailModal}
                        onClose={this.props.detailModalToggle}
                >
                    <CModalHeader closeButton>News Detail</CModalHeader>
                    <CModalBody>

                        <CRow>

                            <CCol xs="12" sm="6">
                                <CCard>
                                    <CCardBody>

                                        <CFormGroup>
                                            <CLabel htmlFor="posttitle">Post Tittle</CLabel>
                                            <CInput name="posttitle"
                                                    value={this.state.poll.polltitle} disabled="disabled"/>
                                        </CFormGroup>

                                        <CFormGroup row>
                                            <CCol md="3">
                                                <CLabel>Answer(Yes/No)</CLabel>

                                                <CFormGroup variant="custom-radio" inline>
                                                    <CInputRadio custom id="inline-radio1"
                                                                 name="yes"
                                                                 onChange={this.handleInputChange} active={this.state.poll.yes===1?'true':null}/>
                                                    <CLabel variant="custom-checkbox" htmlFor="inline-radio1">Yes</CLabel>
                                                </CFormGroup>
                                                <CFormGroup variant="custom-radio" inline>
                                                    <CInputRadio custom id="inline-radio2"
                                                                 name="no"
                                                                 value="1"
                                                                 onChange={this.handleInputChange}
                                                                 value={this.state.no}/>
                                                    <CLabel variant="custom-checkbox" htmlFor="inline-radio2">No</CLabel>
                                                </CFormGroup>
                                            </CCol>

                                        </CFormGroup>

                                        <CFormGroup>
                                            <CLabel htmlFor="postdetails">Post Details</CLabel>
                                            <CInput name="postdetails" value={this.state.poll.postdetails}
                                                    disabled="disabled"/>
                                        </CFormGroup>

                                        <CFormGroup>
                                            <CLabel htmlFor="postconclusion">Post Conclusion</CLabel>
                                            <CInput name="postconclusion"
                                                    value={this.state.poll.postconclusion} disabled="disabled"/>
                                        </CFormGroup>


                                    </CCardBody>

                                </CCard>
                            </CCol>

                        </CRow>

                    </CModalBody>
                </CModal>
            </>
        )
    }

}

export default DetailModal
