import React from 'react'
import {
  CAlert,
  CCard,
  CCardBody,
  CCol,
  CNav,
  CNavItem,
  CNavLink,
  CRow,
  CTabContent,
  CTabPane,
  CTabs,
} from '@coreui/react'
import ProductForm from "./ProductForm";
import ProductTable from "./ProductTable";
import * as axios from "axios";
import DeleteModal from "./modals/DeleteModal";
import SuccessAlert from "../../ui/alerts/SuccessAlert";
import DeleteAlert from "../../ui/alerts/DeleteAlert";

class Product extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      productList: [],
      deleteModal: false,
      showDetailModal: false,
      showSuccessAlert: false,
      showDeleteAlert: false,
    }
  }

  componentWillMount() {

    let
      productSearch = {
        "productCategoryId": null,
        "productCode": "",
        "productName": "",
        "productType": ""
      }

    let page = 1
    let size = 10

    axios.put('http://localhost:8080/api/v1/product/search?page=' + page + "&size=" + size, productSearch)
      // .then(response => response.json())
      .then(response => {

        console.log(response.data.responseDTOList)
        const data = response.data.responseDTOList
        this.setState({
          productList: data
        })

        console.log("o/p" + this.state.productList)
      })
  }

  updateProduct = (productList) => {

    this.setState({
      productList: productList
    })


  }

  showAlert = () => {
    this.setState({
      showSuccessAlert: true
    })

  }

  showDeleteAlert = () => {
    this.setState({
      showSuccessAlert: false,
      showDeleteAlert: true
    })

  }

  editProduct = () => {
  }

  deleteProduct = (id) => {

    this.setState({
        deleteModal: true
      }
    )


    return (

      <DeleteModal deleteModal={this.props.deleteModal}/>
    )

    console.log("deleteProduct" + id)
  }

  addTabController = () => {
    this.setState({
      modal: {
        addNavLink: true,
        addTab: true,
        manageNavLink: false,
        manageTab: false
      }
    })
  }

  manageTabController = () => {
    this.setState({
      modal: {
        addNavLink: false,
        addTab: false,
        manageNavLink: true,
        manageTab: true
      }
    })
  }

  render() {
    return (

      <React.Fragment>
        {this.state.showSuccessAlert === true ?
          <SuccessAlert
            message={"Product is added Successfully..."}/>
          : null}

        {this.state.showDeleteAlert === true ?
          <DeleteAlert
            message={"Product is deleted..."}/>
          : null}

        <CRow>
          <CCol xs="12" md="12" className="mb-4">
            <CCard>
              <CCardBody>
                <CTabs>
                  <CNav variant="tabs">
                    <CNavItem onClick={this.addTabController}>
                      <CNavLink active={this.state.modal.addNavLink}>
                        Add Product
                      </CNavLink>
                    </CNavItem>
                    <CNavItem onClick={this.manageTabController}>
                      <CNavLink active={this.state.modal.manageNavLink}>
                        Manage
                      </CNavLink>
                    </CNavItem>
                  </CNav>

                  <CTabContent>

                    <CTabPane>
                      <ProductForm
                        showAlert={this.showAlert}
                        updateProduct={this.updateProduct}/>
                    </CTabPane>

                    <CTabPane>
                      <ProductTable
                        showDeleteAlert={this.showDeleteAlert}
                        updateProduct={this.updateProduct}
                        deleteProduct={this.deleteProduct}
                        editProduct={this.editProduct}
                        productList={this.state.productList}/>
                    </CTabPane>

                  </CTabContent>
                </CTabs>
              </CCardBody>
            </CCard>
          </CCol>
        </CRow>

      </React.Fragment>
    )
  }


}

export default Product
