import React from 'react'
import {CCard, CCardBody, CCardFooter, CCardHeader, CListGroup, CListGroupItem} from "@coreui/react";
import Cookies from "universal-cookie";
import * as axios from "axios";
import {FacebookIcon, FacebookShareButton} from "react-share";

class Feeds extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            newsList: []
        }

    }

    componentDidMount() {


        const cookies = new Cookies();

        axios.get('http://localhost:3000/usersnews/',
            {
                headers: {

                    Authorization: cookies.get('token')
                }
            }
        )
            .then(response => {

                console.log(response.data)
                const data = response.data

                this.setState({
                    newsList: data
                })

                console.log("o/p" + this.state.newsList)
            })


    }


    render() {
        return (

            (this.state.newsList?.map((news, index) => (
                    <CCol xs="12" sm="6" md="4">
                        <CCard color="primary" className="text-white">
                            <CCardHeader>
                                {news.posttitle}
                            </CCardHeader>
                            <CCardBody>
                                <img src={"https://frozen-refuge-74833.herokuapp.com/PostImage/" + news.images}
                                     style={{width: 500, height: 400}}/>
                                {news.postdetails}
                            </CCardBody>
                            <CCardFooter>
                                <FacebookShareButton
                                    url="https://www.npmjs.com/package/react-share"
                                    quote={news.postdetails}>
                                    <FacebookIcon logoFillColor="white"/>
                                </FacebookShareButton>
                            </CCardFooter>
                        </CCard>
                    </CCol>
                ))
            )


        )
    }


}

export default Feeds
