import {CBadge, CButton, CDataTable, CModal, CModalBody, CModalFooter, CModalHeader} from "@coreui/react";
import React, {useState} from "react";
import DeleteModal from "./DeleteModal";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'productQuantity',
  "costPrice",
  "sellingPrice",
  'actions']

const ShowDeleteModal = (modal, toggle) => {

  return (

    <CModal
      show={modal}
      onClose={toggle}
    >
      <CModalHeader closeButton>Modal title</CModalHeader>
      <CModalBody>
        Lorem ipsum dolor...
      </CModalBody>
      <CModalFooter>
        <CButton color="primary">Do Something</CButton>{' '}
        <CButton
          color="secondary"
          onClick={toggle}
        >Cancel</CButton>
      </CModalFooter>
    </CModal>

  )
}

const ProductTable = (props) => {

  let productList = props.productList
  const [modal, setModal] = useState(false);
  const toggle = (id) => {
    setModal(!modal);

  }


  return (
    <React.Fragment>
      <DeleteModal
        id={id}
        modal={modal}
        toggle={toggle}/>

      <CDataTable
        items={productList}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'status':
            (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>
                  {item.status}
                </CBadge>
              </td>
            ),
          'actions':
            (item) => (
              <td>
                <CButton active block color="primary" aria-pressed="true">Edit</CButton>
                <CButton key={item.id} active block color="danger" aria-pressed="true"
                         onClick={toggle(item.id)}>Delete</CButton>
                {/*<CButton key={item.id} active block color="danger" aria-pressed="true"*/}
                {/*         onClick={() => props.deleteProduct(item.id)}>Delete</CButton>*/}
              </td>
            )

        }}
      />
    </React.Fragment>
  )
}

export default ProductTable
