import React from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CFormGroup,
  CInput,
  CLabel,
  CRow,
  CSelect,
  CTextarea,
} from "@coreui/react";
import CIcon from "@coreui/icons-react";
import * as axios from "axios";

class SupplierForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      supplierName: "",
      supplierAddress: "",
      supplierPanNumber: "",
      supplierPhoneNumber: "",
      supplierMobileNumber: "",
      supplierEmail: "",
      supplierWebsiteUrl: "",
      supplierList: [],
      supplierCategoryList:[]
    }
  }

  componentDidMount() {

    axios.get('http://localhost:8080/api/v1/supplier/active/min')
      // .then(response => response.json())
      .then(response => {
        const data = response.data

        this.setState({
          supplierList: data
        })
        console.log(this.state.supplierList)
      })

  }

  handleInputChange = (e) => {

    const {name, value} = e.target
    this.setState({
      [name]: value
    })

    if(e.target.name==='supplierId' && e.target.value!=='-1'){

      const supplierId = e.target.value

      axios.get(`http://localhost:8080/api/v1/supplier-category/active/${supplierId}`)
        .then(response => {
          const data = response.data

          this.setState({
            supplierCategoryList: data
          })
          console.log(this.state.supplierCategoryList)
        })


    }

  }

  handleFormSubmit = (e) => {
    e.preventDefault()

    let supplier = {
      supplierId: this.state.supplierId,
      supplierCategoryId: this.state.supplierCategoryId,
      supplierType: this.state.supplierType,
      supplierName: this.state.supplierName,
      supplierDescription: this.state.supplierDescription,
      supplierQuantity: this.state.supplierQuantity,
      supplierCostPrice: this.state.supplierCostPrice,
      supplierSellingPrice: this.state.supplierSellingPrice,
      supplierDiscountAmount: this.state.supplierDiscountAmount,
      supplierDiscountPercentage: this.state.supplierDiscountPercentage,
    }

    axios.post('http://localhost:8080/api/v1/supplier', supplier)
      // .then(response => response.json())
      .then(response => {

        let
          supplierSearch = {
            "supplierId": "",
            "supplierName": "",
          }

        let page = 1
        let size = 10

        axios.put('http://localhost:8080/api/v1/supplier/search?page=' + page + "&size=" + size, supplierSearch)
          // .then(response => response.json())
          .then(response => {

            console.log(response.data.responseDTOList)
            const data = response.data.responseDTOList

            this.props.updateSupplier(data)
          })

      })


  }


  render() {
    return (

      <CRow>

        <CCol xs="12" sm="6">
          <CCard>
            <CCardBody>

              <CFormGroup>
                <CLabel htmlFor="supplierId">Supplier</CLabel>
                <CSelect custom id="supplierId" name="supplierId"
                         value={this.state.supplierId}
                         onChange={this.handleInputChange}
                         placeholder="Enter your company name"
                         required="required">

                  <option value="-1">Please Select Supplier</option>
                  {this.state.supplierList.map(supplier => {
                    return (
                      <option value={supplier.value}>{supplier.label}</option>
                    )
                  })}

                </CSelect>

              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="supplierCategory">Supplier Category</CLabel>
                <CSelect custom id="supplierCategoryId" name="supplierCategoryId"
                         value={this.state.supplierCategoryId}
                         onChange={this.handleInputChange}
                         required="required">

                  {this.state.supplierId===null?
                  <option value="-1">Please Select Supplier first</option>:
                  <option value="-1">Please Select Supplier Category</option>}
                  {this.state.supplierCategoryList.map(supplierCategory => {
                    return (
                      <option value={supplierCategory.value}>{supplierCategory.label}</option>
                    )
                  })}

                </CSelect>

              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="supplierCategory">Supplier Type</CLabel>
                <CInput name="supplierType" id="supplierType"
                        value={this.state.supplierType}
                        onChange={this.handleInputChange}
                        placeholder="Enter Supplier Type"
                        required="required"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="supplierName">Supplier Name</CLabel>
                <CInput name="supplierName" id="supplierName"
                        value={this.state.supplierName}
                        onChange={this.handleInputChange}
                        placeholder="Enter Supplier Name"
                        required="required"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="quantity">Quantity</CLabel>
                <CInput id="quantity" name="supplierQuantity"
                        value={this.state.supplierQuantity}
                        onChange={this.handleInputChange}
                        placeholder="Quantity"/>
              </CFormGroup>

              <CFormGroup>
                <CLabel htmlFor="supplierDescription">Supplier Description</CLabel>
                <CTextarea name="supplierDescription" id="supplierDescription"
                           value={this.state.supplierDescription}
                           onChange={this.handleInputChange}
                           placeholder="Enter Supplier Description"
                           required="required"/>
              </CFormGroup>


            </CCardBody>

          </CCard>
        </CCol>

        <CCol xs="12" sm="6">
          <CCard>
            <CCardBody>
              <CFormGroup>
                <CLabel htmlFor="costPrice">Cost Price(Rs.)</CLabel>
                <CInput id="costPrice" name="supplierCostPrice"
                        value={this.state.supplierCostPrice}
                        onChange={this.handleInputChange}
                        placeholder="Enter your Cost Price"
                        required="required"/>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="sellingPrice">Selling Price(Rs.)</CLabel>
                <CInput id="sellingPrice" name="supplierSellingPrice"
                        value={this.state.supplierSellingPrice}
                        onChange={this.handleInputChange}
                        placeholder="Enter Selling Price"
                        required="required"/>
              </CFormGroup>
              <CFormGroup>
                <CLabel htmlFor="supplierDiscountAmount">Discount Amount</CLabel>
                <CInput id="supplierDiscountAmount" name="supplierDiscountAmount"
                        value={this.state.supplierDiscountAmount}
                        onChange={this.handleInputChange}
                        placeholder="Enter Discount Amount"
                        required="required"/>
              </CFormGroup>
              <CFormGroup row className="my-0">
                <CCol xs="8">
                  <CFormGroup>
                    <CLabel htmlFor="supplierDiscountPercentage">Discount Percentage(%)</CLabel>
                    <CInput id="supplierDiscountPercentage" name="supplierDiscountPercentage"
                            value={this.state.supplierDiscountPercentage}
                            onChange={this.handleInputChange}
                            placeholder="Enter your city"
                            required="required"/>
                  </CFormGroup>
                </CCol>
              </CFormGroup>

            </CCardBody>
            <CCardFooter>
              <CButton type="submit" onClick={this.handleFormSubmit} size="sm" color="primary"><CIcon
                name="cil-scrubber"/> Submit</CButton>
              <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban"/> Reset</CButton>
            </CCardFooter>

          </CCard>
        </CCol>

      </CRow>


    )
  }
}

export default SupplierForm
