import React from 'react'
import {CButton, CCard, CCardBody, CForm, CFormGroup, CInput, CLabel,} from "@coreui/react";
import * as axios from "axios";
import CIcon from "@coreui/icons-react";
import Cookies from "universal-cookie";
import {BASE_URL} from "../../api/Api";

class KidsForm extends React.Component {
    constructor(props) {
        super(props);

        console.log(props)

        this.state = {
            fullname: "",
            age: "",
            gender: "",
            parentName: "",
            images:"",
            selectedFile:""
        }
    }

    handleInputChange = (e) => {

        const {name, value} = e.target
        this.setState({
            [name]: value
        })

    }


    imageUploader = (e) => {
        e.preventDefault()

        console.log(e.target.files)

        this.setState({
            selectedFile: e.target.files[0],
        })

    }

    handleFormSubmit = (e) => {
        e.preventDefault()


        let formData = new FormData()
        formData.append('images', this.state.selectedFile)
        formData.append('posttitle', this.state.posttitle);
        formData.append('postcategory', this.state.postcategory);
        formData.append('postdetails', this.state.postdetails);
        formData.append('postconclusion', this.state.postconclusion)


        axios.post(BASE_URL+'/posts/',
            formData,
            {
                headers: {
                    Authorization: cookies.get('token'),
                }
            })

        let options = [this.state.options1,
            this.state.options2,
            this.state.options3,
            this.state.options4,
        ]

        let requestDTO = {
            kids: this.state.kids,
            description: this.state.description,
            type: this.state.type,
            level: this.state.level,
            correctANswer: this.state.correctAnswer,
            options: options
        }

        const cookies = new Cookies();

        console.log("token===", cookies.get('token'))

        axios.post(BASE_URL + '/Kids/', requestDTO,
            {
                headers: {
                    Authorization: cookies.get('token'),
                },
            })
            .then(response => {

                axios.get(BASE_URL + '/Kids/',
                    {
                        headers: {
                            Authorization: cookies.get('token'),
                        },
                    })
                    .then(response => {

                        console.log(response.data)
                        const data = response.data

                        this.props.updateKids(data)
                    })

                this.props.manageTabController()

                this.props.showAlert()

            })
            .catch(function (error) {
                if (error.response) {
                    // Request made and server responded
                    console.log(error.response);
                }

                // throw new Error('Sorry Internal Server Error')
                this.showError(error.response)
            })

    }


    render() {
        return (


            <CCard>
                <CCardBody>

                    <CForm onSubmit={this.handleFormSubmit}>
                        <CFormGroup>
                            <CLabel htmlFor="fullname">Full name</CLabel>
                            <CInput name="fullname" id="fullname"
                                    value={this.state.fullname}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Full Name"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="age">Age</CLabel>
                            <CInput name="age" id="age"
                                    value={this.state.age}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Age"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="gender">Gender</CLabel>
                            <CInput id="gender" name="gender"
                                    value={this.state.gender}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Gender"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="parentName">Parent Name</CLabel>
                            <CInput name="parentName" id="parentName"
                                    value={this.state.parentName}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Parent Name"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="Correct Answer">Correct Answer</CLabel>
                            <CInput name="correctAnswer" id="correctAnswer"
                                    value={this.state.correctAnswer}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Correct Answer"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CLabel htmlFor="option1">Options</CLabel>
                            <CInput name="options1" id="options1"
                                    value={this.state.options1}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Option 1"
                                    required="required"/>
                            <CInput name="options2" id="options2"
                                    value={this.state.options2}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Option 2"
                                    required="required"/>
                            <CInput name="options3" id="options3"
                                    value={this.state.options3}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Option 3"
                                    required="required"/>
                            <CInput name="options4" id="options4"
                                    value={this.state.options4}
                                    onChange={this.handleInputChange}
                                    placeholder="Enter Option 4"
                                    required="required"/>
                        </CFormGroup>

                        <CFormGroup>
                            <CButton type="submit" size="sm" color="primary"><CIcon
                                name="cil-scrubber"/> Submit</CButton>
                        </CFormGroup>

                    </CForm>
                </CCardBody>

            </CCard>


        )
    }
}

export default KidsForm
