import {CBadge, CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle} from "@coreui/react";
import React from "react";
import productData from "./ProductData";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'status',
  'actions']


class ProductTable extends React.Component {

  constructor(props) {
    super(props);

  }

  componentWillMount() {
    this.fetchAllNews()
  }

  fetchAllNews() {
    axios.get('http://localhost:8080/api/v1/product/search/')
      // .then(response => response.json())
      .then(response => {
        console.log("newsList=" + response.data)

        this.setState({
          newsList: response.data
        })
        console.log(this.state.newsList)
      })
  }


  render() {
    return (
      <CDataTable
        items={productData}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'status':
            (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>
                  {item.status}
                </CBadge>
              </td>
            ),
          'actions':
            () => (
              <td>
                <CDropdown className="m-1 btn-group">
                  <CDropdownToggle color="secondary">
                    Actions
                  </CDropdownToggle>
                  <CDropdownMenu>
                    <CDropdownItem>View</CDropdownItem>
                    <CDropdownItem>Edit</CDropdownItem>
                    <CDropdownItem>Delete</CDropdownItem>
                  </CDropdownMenu>
                </CDropdown>
              </td>
            )

        }}
      />
    )
  }
}

export default ProductTable
