import React from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCol,
  CFormGroup,
  CInput,
  CLabel,
  CModal,
  CModalBody,
  CModalFooter,
  CModalHeader,
  CRow,
  CTextarea
} from "@coreui/react";
import * as axios from "axios";

class DetailModal extends React.Component {
  constructor(props) {
    super(props);

    this.state={
      supplier: {}
    }
  }


  componentWillMount() {
    axios.get(`http://localhost:8080/api/v1/supplier/detail/${this.props.selectedItem}`)
      .then(response => {

        this.setState({
          supplier:response.data
        })

      })
  }

  render() {

    return (
      <>
        <CModal size={'xl'}
                show={this.props.detailModal}
          onClose={this.props.detailModalToggle}
        >
          <CModalHeader closeButton>Product Detail</CModalHeader>
          <CModalBody>

            <CRow>

              <CCol xs="12" sm="6">
                <CCard>
                  <CCardBody>

                    {/*<CFormGroup>*/}
                    {/*  <CLabel htmlFor="supplierId">Supplier</CLabel>*/}
                    {/*  <CSelect custom id="supplierId" name="supplierId"*/}
                    {/*           placeholder="Enter your company name">*/}

                    {/*    <option value="-1">Please Select Supplier</option>*/}
                    {/*    {this.state.supplierList.map(supplier => {*/}
                    {/*      return (*/}
                    {/*        <option value={supplier.value}>{supplier.label}</option>*/}
                    {/*      )*/}
                    {/*    })}*/}

                    {/*  </CSelect>*/}

                    {/*</CFormGroup>*/}

                    <CFormGroup>
                      <CLabel htmlFor="productCategory">Product Category</CLabel>
                      <CInput name="productCategory"
                              placeholder="Enter Product Category"/>
                    </CFormGroup>
                    <CFormGroup>
                      <CLabel htmlFor="productCategory">Product Type</CLabel>
                      <CInput name="productType"
                              placeholder="Enter Product Type"/>
                    </CFormGroup>

                    <CFormGroup>
                      <CLabel htmlFor="supplierName">Supplier Name</CLabel>
                      <CInput name="supplierName" value={this.state.supplier.supplierName}/>
                    </CFormGroup>

                    <CFormGroup>
                      <CLabel htmlFor="quantity">Quantity</CLabel>
                      <CInput name="productQuantity"
                              placeholder="Quantity"/>
                    </CFormGroup>

                    <CFormGroup>
                      <CLabel htmlFor="productDescription">Product Description</CLabel>
                      <CTextarea name="productDescription"
                                 placeholder="Enter Product Description"/>
                    </CFormGroup>


                  </CCardBody>

                </CCard>
              </CCol>

              <CCol xs="12" sm="6">
                <CCard>
                  <CCardBody>
                    <CFormGroup>
                      <CLabel htmlFor="costPrice">Cost Price(Rs.)</CLabel>
                      <CInput name="productCostPrice"
                              placeholder="Enter your Cost Price"/>
                    </CFormGroup>
                    <CFormGroup>
                      <CLabel htmlFor="sellingPrice">Selling Price(Rs.)</CLabel>
                      <CInput name="productSellingPrice"
                              placeholder="Enter Selling Price"/>
                    </CFormGroup>
                    <CFormGroup>
                      <CLabel htmlFor="productDiscountAmount">Discount Amount</CLabel>
                      <CInput  name="productDiscountAmount"
                              placeholder="Enter Discount Amount"/>
                    </CFormGroup>
                    <CFormGroup row className="my-0">
                      <CCol xs="8">
                        <CFormGroup>
                          <CLabel htmlFor="productDiscountPercentage">Discount Percentage(%)</CLabel>
                          <CInput  name="productDiscountPercentage"
                                  placeholder="Enter your city"/>
                        </CFormGroup>
                      </CCol>
                    </CFormGroup>

                  </CCardBody>

                </CCard>
              </CCol>

            </CRow>

          </CModalBody>
          <CModalFooter>

            <CButton
              color="secondary"
              onClick={this.props.toggle}
            >Cancel</CButton>{' '}
          </CModalFooter>
        </CModal>
      </>
    )
  }

}

export default DetailModal
