import {CBadge, CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle} from "@coreui/react";
import React from "react";

const getBadge = status => {
  switch (status) {
    case 'Active':
      return 'success'
    case 'Inactive':
      return 'secondary'
    case 'Pending':
      return 'warning'
    case 'Banned':
      return 'danger'
    default:
      return 'primary'
  }
}
const fields = [
  'productName',
  'productCategory',
  'productType',
  'productQuantity',
  "costPrice",
  "sellingPrice",
  'actions']

class ProductTable extends React.Component {
  constructor(props) {
    super(props);

    console.log(props.productList)

    let productList=props.productList

    console.log("table data--------"+productList)

  }

  render() {

    console.log("render="+this.productList)

    return (
      <CDataTable
        items={this.productList}
        fields={fields}
        bordered
        itemsPerPage={10}
        pagination
        scopedSlots={{
          'status':
            (item) => (
              <td>
                <CBadge color={getBadge(item.status)}>
                  {item.status}
                </CBadge>
              </td>
            ),
          'actions':
            () => (
              <td>
                <CDropdown className="m-1 btn-group">
                  <CDropdownToggle color="secondary">
                    Actions
                  </CDropdownToggle>
                  <CDropdownMenu>
                    <CDropdownItem>View</CDropdownItem>
                    <CDropdownItem>Edit</CDropdownItem>
                    <CDropdownItem>Delete</CDropdownItem>
                  </CDropdownMenu>
                </CDropdown>
              </td>
            )

        }}
      />
    )
  }
}

export default ProductTable
