import {CDataTable, CDropdown, CDropdownItem, CDropdownMenu, CDropdownToggle, CImg} from "@coreui/react";
import React, {useState} from "react";
import DeleteModal from "./modals/DeleteModal";
import DetailModal from "./modals/DetailModal";
import EditModal from "./modals/EditModal";
import { FacebookShareButton, FacebookIcon } from "react-share"
import Cookies from "universal-cookie";
import * as axios from "axios";

const getBadge = status => {
    switch (status) {
        case 'Y':
            return 'success'
        case 'N':
            return 'secondary'
        case 'Active':
            return 'success'
        case 'Inactive':
            return 'secondary'
        case 'Pending':
            return 'warning'
        case 'Banned':
            return 'danger'
        default:
            return 'primary'
    }
}

const deleteImage=(id)=>{

    const cookies = new Cookies();

    axios.put(`https://frozen-refuge-74833.herokuapp.com/deleteImage/${id}`,
        {
            headers: {

                Authorization: cookies.get('token')
            }
        }
    )
        .then(response => {

            const data = response.data
            this.props.updateNews(data)

        })


}

const fields = [

    'posttitle',
    'postcategory',
    'postdetails',
    'postconclusion',
    'images',
    'share',
    'actions']

const NewsTable = (props) => {

    let newsList = props.newsList
    const [modal, setModal] = useState(false);
    const [detailModal, setDetailModal] = useState(false);
    const [editModal, setEditModal] = useState(false);
    const [selectedItem, setSelectedItem] = useState(false);
    const [editItem, setEditItem] = useState(false);

    const detailModalToggle = (id) => {
        setSelectedItem(id)
        setDetailModal(!detailModal);

    }

    const editModalToggle = (id) => {
        setEditItem(id)
        setEditModal(!editModal);

    }

    const toggle = (id) => {

        setSelectedItem(id)
        setModal(!modal);

    }


    return (
        <React.Fragment>
            {selectedItem ? <DetailModal
                selectedItem={selectedItem}
                detailModal={detailModal}
                detailModalToggle={detailModalToggle}/> : null}

            {editItem ? <EditModal
                updateNews={props.updateNews}
                editItem={editItem}
                editModal={editModal}
                editModalToggle={editModalToggle}/> : null}
            <DeleteModal
                showDeleteAlert={props.showDeleteAlert}
                updateNews={props.updateNews}
                selectedItem={selectedItem}
                modal={modal}
                toggle={toggle}/>

            <CDataTable
                items={newsList}
                fields={fields}
                bordered
                itemsPerPage={10}
                pagination
                scopedSlots={{
                    'images':
                        (item) => (

                            <td>
                                <img src={"https://frozen-refuge-74833.herokuapp.com/PostImage/"+item.images}
                                     style={{width: 500,height:400}}/>
                                     <button onClick={()=>deleteImage(item._id)}>Delete Image</button>
                            </td>
                        ),
                    'share':
                        (item) => (

                            <td>
                                <FacebookShareButton url="https://www.facebook.com/rupakchaulagain">
                                    <FacebookIcon logoFillColor="white" />
                                </FacebookShareButton>
                            </td>
                        ),
                    'actions':
                        (item) => (
                            <td>

                                <CDropdown className="m-1 btn-group">
                                    <CDropdownToggle color="primary">
                                        Actions
                                    </CDropdownToggle>
                                    <CDropdownMenu>

                                        <CDropdownItem
                                            onClick={() => detailModalToggle(item._id)}>View</CDropdownItem>

                                        <CDropdownItem
                                            onClick={() => editModalToggle(item._id)}>Edit</CDropdownItem>

                                        <CDropdownItem
                                            key={item._id}
                                            onClick={() => toggle(item._id)}>Delete</CDropdownItem>
                                    </CDropdownMenu>
                                </CDropdown>


                            </td>
                        )

                }}
            />
        </React.Fragment>
    )
}

export default NewsTable
