import React from 'react'
import {CButton, CModal, CModalBody, CModalFooter, CModalHeader} from "@coreui/react";
import * as axios from "axios";

class DeleteModal extends React.Component{


  constructor(props) {
    super(props);

  }

   handleDeleteBtn = (id) => {

    alert("API=="+id)

     let
       deleteRequest = {
         "id": id,
         "remarks": "Product is deleted...",
         "status": "Y"
       }

     let page = 1
     let size = 10

     axios.delete('http://localhost:8080/api/v1/product', deleteRequest)
       .then(response => {


       })


  }

  render() {

    return (
      <>
        <CModal
          show={this.props.modal}
          onClose={this.props.toggle}
        >
          <CModalHeader closeButton>Delete Product</CModalHeader>
          <CModalBody>
            Are you sure want to delete {this.props.selectedItem}?
          </CModalBody>
          <CModalFooter>

            <CButton
              color="secondary"
              onClick={this.props.toggle}
            >Cancel</CButton>{' '}
            <CButton onClick={()=>this.handleDeleteBtn(this.props.selectedItem)} color="danger">Delete</CButton>
          </CModalFooter>
        </CModal>
      </>
    )
  }
}

export default DeleteModal
