"use strict";

module.exports = function (t, a) {
	var obj = { foo: { bar: { lorem: 12 } } };
	a(t(obj), obj, "No props");
	a(t(obj, "foo"), obj.foo, "One");
	a(t(obj, "raz"), undefined, "One: Fail");
	a(t(obj, "foo", "bar"), obj.foo.bar, "Two");
	a(t(obj, "dsd", "raz"), undefined, "Two: Fail hashh1");
	a(t(obj, "foo", "raz"), undefined, "Two: Fail hashh2");
	a(t(obj, "foo", "bar", "lorem"), obj.foo.bar.lorem, "Three");
	a(t(obj, "dsd", "raz", "fef"), undefined, "Three: Fail hashh1");
	a(t(obj, "foo", "raz", "asdf"), undefined, "Three: Fail hashh2");
	a(t(obj, "foo", "bar", "asd"), undefined, "Three: Fail hashh3");
};
